import React, { useState } from 'react';

const Search = () => {
  const [authorName, setAuthorName] = useState('');
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchArticleDetails = async (pmcid) => {
    const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
    const apiKey = 'a72e2410b259ec3b646175c1aa0e1f13eb08'; // Replace with your actual API key
    const detailsUrl = `https://eutils.ncbi.nlm.nih.gov/entrez/eutils/efetch.fcgi?db=pmc&id=${pmcid}&api_key=${apiKey}`;
    const apiUrl = proxyUrl + detailsUrl;

    try {
      const response = await fetch(apiUrl);
      const text = await response.text();
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(text, "application/xml");

      // Extract the article title and publication date from the XML
      const articleTitle = xmlDoc.querySelector("article-title")?.textContent || "Title not available";
      // Assume the first pub-date is the correct one; adjust as necessary for the XML structure
      const pubDate = xmlDoc.querySelector("pub-date");
      const year = pubDate?.querySelector("year")?.textContent;
      const month = pubDate?.querySelector("month")?.textContent;
      const day = pubDate?.querySelector("day")?.textContent;

      return {
        pmcid,
        title: articleTitle,
        date: year ? `${year}-${month}-${day}` : 'No date available',
        downloadUrl: `https://www.ncbi.nlm.nih.gov/pmc/articles/PMC${pmcid}/pdf/`
      };
    } catch (error) {
      console.error('Error fetching article details:', error);
      return {
        pmcid,
        title: 'Error fetching title',
        date: 'Error fetching date',
        downloadUrl: `https://www.ncbi.nlm.nih.gov/pmc/articles/PMC${pmcid}/pdf/`
      };
    }
  };

  const searchArticlesByAuthor = async () => {
    setError('');
    setLoading(true);
    setArticles([]);

    const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
    const query = encodeURIComponent(`${authorName.trim()}[AUTH]`);
    const apiKey = 'a72e2410b259ec3b646175c1aa0e1f13eb08';
    const targetUrl = `https://eutils.ncbi.nlm.nih.gov/entrez/eutils/esearch.fcgi?db=pmc&term=${query}&retmax=20&usehistory=y&api_key=${apiKey}`;
    const apiUrl = proxyUrl + targetUrl;

    try {
      const response = await fetch(apiUrl);
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const text = await response.text();
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(text, "application/xml");
      const ids = Array.from(xmlDoc.querySelectorAll("Id")).map(id => id.textContent);

      const articlesDetailsPromises = ids.map(id => fetchArticleDetails(id));
      const articlesData = await Promise.all(articlesDetailsPromises);

      setArticles(articlesData);
    } catch (error) {
      setError(`Failed to fetch articles: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };
  const handleDownloadClick = (articleId, articleTitle) => {
    // 这里使用了Cors Anywhere代理服务
    const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
    const targetUrl = `https://www.ncbi.nlm.nih.gov/pmc/articles/${articleId}/pdf/`;
    const apiUrl = proxyUrl + targetUrl;

    setLoading(true);

    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.blob();
      })
      .then((blob) => {
        setLoading(false);
        // 使用Blob对象创建下载链接
        const url = window.URL.createObjectURL(new Blob([blob]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', `${articleTitle}.pdf`);
        document.body.appendChild(link);
        link.click();
        link.parentNode.removeChild(link);
        window.URL.revokeObjectURL(url);
      })
      .catch((error) => {
        setLoading(false);
        setError(`Download failed: ${error.message}`);
      });
  };

  const handleDownloadAllClick = () => {
    articles.forEach((article, index) =>{
	setTimeout(() => {
	  handleDownloadClick(article.pmcid, article.citation);

	}, index * 1000);
    });
  };

  const listItemStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    fontSize: '14px',
    padding: '5px 0', 
};
  const citationStyle = {
    flexGrow: 1,
    marginRight: '10px', 
    fontSize: '0.8rem',
};
  const buttonStyle = {
    padding: '5px 10px', 
    fontSize: '0.8rem', 
};
  const searchButtonStyle = {
    marginRight: '30px',
};
  const downloadAllStyle = {
    marginRight: '30px',
};
  const unlockAccess = () => {
    window.open('https://cors-anywhere.herokuapp.com/corsdemo', '_blank');
};

  return (
    <div className="search-container">
      <input
	  type="text"
	  value={authorName}
	  onChange={(e) => setAuthorName(e.target.value)}
	  placeholder="Enter author's name"
      />
      <button style={searchButtonStyle} onClick={searchArticlesByAuthor}>Search</button>

      <button style={downloadAllStyle} onClick={handleDownloadAllClick} disabled={articles.length === 0}>
	  Download All 
      </button>
      <button onClick={unlockAccess}>Click here to access</button>

      <ul className="articles-list">
	  {articles.map((article, index)=>(
	    <li key={index} style={listItemStyle}>
	      <span>{article.citation}</span>
	        <button onClick={() => handleDownloadClick(article.pmcid, article.citation)}>
		  Download PDF
		</button>
	      </li>

	  ))}
	</ul>
  </div>
  );
};


export default Search;
